mapper.select:
SELECT c.movieTitle, c.reviewNo, c.memberId, c.movieNo, c.reviewTitle, c.reviewContent, c.reviewView, c.reviewDate from ( select /*+INDEX_ASC(review PK_review )*/ rownum rn, b.movieTitle, a.reviewNo, a.movieNo, a.memberId, a.reviewTitle, a.reviewContent, a.reviewView, a.reviewDate
from review a, movie b where
a.movieNo = b.movieNo and rownum <= #{pageNum} * #{amount} )c WHERE c.rn > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE 	review 
SET		reviewTitle = #{reviewTitle}, 	reviewContent = #{reviewContent}
WHERE 	reviewNo = #{reviewNo}

UPDATE review
SET reviewView = reviewView+1
where reviewNo = #{reviewNo}

mapper.select:
   	select m.movieTitle
		from review r,movie m
		where r.movieNo = m.movieNo and reviewNo = #{reviewNo}
